mapper.select:
SELECT SEQ, NAME,PRICE, NEWNAME, ORIGINNAME, INFO
FROM CC_COFFEE
WHERE SEQ = #{seq}

SELECT VISITCOUNT
FROM CC_MEMBER
WHERE ID = #{id}

mapper.update:
UPDATE CC_MEMBER
SET VISITCOUNT = VISITCOUNT + 1
WHERE ID = #{id}

UPDATE CC_COFFEE
SET ORDER_COUNT = ORDER_COUNT + 1
WHERE SEQ = #{seq}

mapper.select:
SELECT SEQ, NAME, PRICE, NEWNAME
FROM CC_COFFEE
WHERE KIND = #{kind}
